import React, { useState, useCallback } from 'react';
import Button from './Button';

function Cups() {
  const [cups, setCups] = useState('');
  const [copied, setCopied] = useState(false);

  // No es necesario usar useCallback aquí ya que la función no depende del estado/props
  const generarParteNumerica = (distribuidora) => {
    const numeroSuministro = Math.floor(Math.random() * 1e12).toString().padStart(12, '0');
    return distribuidora + numeroSuministro;
  };

  // Esta función tampoco necesita useCallback por la misma razón
  const calcularCaracteresControl = (numero) => {
    const tablaEquivalencia = "TRWAGMYFPDXBNJZSQVHLCKE";
    const numeroEntero = BigInt(numero);
    const R0 = numeroEntero % 529n;
    const C = R0 / 23n;
    const R = R0 % 23n;
    return tablaEquivalencia[Number(C)] + tablaEquivalencia[Number(R)];
  };

  const generarCups = useCallback(() => { // Eliminamos los parámetros predeterminados y usamos argumentos directos dentro de la función
    const pais = "ES";
    const distribuidora = "1234";
    const ntOpcional = true; // Ajusta esto según sea necesario
    const parteNumerica = generarParteNumerica(distribuidora);
    const caracteresControl = calcularCaracteresControl(parteNumerica);
    let cupsFormateado = `${pais} ${distribuidora} ${parteNumerica.substring(0, 4)} ${parteNumerica.substring(4, 8)} ${parteNumerica.substring(8, 12)} ${caracteresControl}`;
    if (ntOpcional) {
      const n = Math.floor(Math.random() * 10);
      const opcionesT = ['F', 'P', 'R', 'C', 'X', 'Y', 'Z'];
      const t = opcionesT[Math.floor(Math.random() * opcionesT.length)];
      cupsFormateado += ` ${n} ${t}`;
    }
    setCups(cupsFormateado);
  }, []); // Las dependencias están vacías ya que la función no depende del estado externo

  // Actualizado para usar un botón en lugar de useEffect para generar el CUPS inicial
  const handleCopyToClipboard = () => {
    navigator.clipboard.writeText(cups);
    setCopied(true);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div>
          <input type="text" value={cups} readOnly style={{ width: '400px', height: '60px', padding: '8px', marginRight: '8px', fontSize: '20px' }} />
          <button onClick={handleCopyToClipboard} style={{ backgroundColor: 'white', color: 'black', padding: '8px', border: '1px solid black', cursor: 'pointer', width: '100px', height: '60px', fontSize: '20px' }}>Copiar</button>
          {copied && <p style={{ color: 'white' }}>¡CUPS copiado al portapapeles!</p>}
        </div>
        {/* Botón actualizado para generar CUPS */}
        <Button onClick={generarCups} text="Generar CUPS" />
      </header>
    </div>
  );
}

export default Cups;
